package Conditional;

public class Calculator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1=79;
		int num2=78;
		char ch='/';
		/*char ch1='-';
		char ch2='*';
		char ch3='/';*/
	if(ch=='+') {
		System.out.println(num1+num2);
	}
	else if(ch=='-') {
		System.out.println(num1-num2);
	}
	else if(ch=='*') {
		
		System.out.println(num1*num2);
	}
	else if(ch=='/') {
		System.out.println(num1/num2);
	}
	else {
		System.out.println("bye");
	}

	}

}
